mapper.select:
select BOOK_ID,TITLE,IMAGE_MEDIUM from book_info
where 1 = 1

  and AGE_TYPE = #{ageType,jdbcType=INTEGER}


  and THEME_TYPE = #{themeType,jdbcType=INTEGER}


  and TITLE like concat('%',#{title},'%')

mapper.update:
update book_info set BOOK_COUNTS = case
WHEN BOOK_COUNTS >= #{count} then BOOK_COUNTS - #{count}
else BOOK_COUNTS
end
WHERE BOOK_ID = #{id};
